import React, { useState } from 'react'
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
const FunApp1 = () => {
    const [value , changedValue]= useState(0);
    const decrement=()=>{
        if(value ===0){
            alert("Can't decrease the value below 0");
        }
        else{
            changedValue(value-1);
        }
    }
    const increment=()=>{
        changedValue(value+1);
    }
    
    return (
        <>
            <div className="funApp">
                <div className="title">
                    <h1>Fun App</h1>
                </div>
                <div className="container">
                    <div className="display">
                        <h1>{value}</h1>
                    </div>
                    <div className="button">
                        <button onClick={decrement}><RemoveIcon/></button>
                        <button onClick={increment}><AddIcon/></button>
                    </div>
                    {/* <div className="dummy">
                        <h1>1</h1>
                    </div> */}
                </div>
            </div>
        </>
    )
}

export default FunApp1
